    private void discountBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_discountBtnActionPerformed
      double loan=0,interestRate,discount=0,netamt=0,interest=0;
      String loanStr=txtLamount.getText().trim();
      if(loanStr.equals(""))
      {JOptionPane.showMessageDialog(null, "Enter the loan amount");}
      else{
          try{
              loan=Double.parseDouble(loanStr);
              interestRate=Double.parseDouble(txtRate.getText());
              interest=loan*interestRate;
              if(loan<=1000000)
              {discount=loan*.0020;}
              else if(loan>1000000)
              {discount=loan*.0025;}
              netamt=interest-discount;
              txtDisc.setText(""+discount);
              netAmt.setText(""+netamt);}
          catch(Exception e)
          {JOptionPane.showMessageDialog(null, "Enter loan in numeric form");}}



}//GEN-LAST:event_discountBtnActionPerformed

