    private void clearBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_clearBtnActionPerformed
 txtDisc.setText("");
 txtLamount.setText("");
 txtRate.setText("");
 netAmt.setText("");
 optCar.setSelected(true);
 optHouse.setSelected(false);
 optEdu.setSelected(false);

    }//GEN-LAST:event_clearBtnActionPerformed

    private void interestBtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_interestBtnActionPerformed

      if(optCar.isSelected())
         {txtRate.setText("0.10");}
      else if(optHouse.isSelected())
        {txtRate.setText("0.085");}
      else if(optEdu.isSelected())
      {txtRate.setText("0.05");}

      

    }//GEN-LAST:event_interestBtnActionPerformed

